where.trim:
T.NOTICE_FLG = 'N'

	AND T.BOARD_KEY = #{boardKey}


	AND (VIEW_START_DT IS NULL OR VIEW_START_DT < SYSDATE)
	AND (VIEW_END_DT IS NULL OR VIEW_END_DT > SYSDATE)


	AND T.FRST_OPER_DT >= #{searchStartFrstOperDt}


	AND T.FRST_OPER_DT <= TO_DATE(#{searchEndFrstOperDt}, 'YYYY-MM-DD') + 1


	AND T.CATGR = #{searchCatgr}


	AND T.DELETE_FLG = #{searchDeleteFlg}



		AND 




						AND  T.TITLE LIKE '%' || #{item} || '%'




						AND  T.CONTENT LIKE '%' || #{item} || '%'



					T.WRITER LIKE '%' || #{searchValue} || '%'

mapper.update:
UPDATE TB_ARTICLE SET 
	CATGR = #{catgr}
	,TITLE = #{title}
	,CONTENT = #{content}
	,WRITER = #{writer}
	,WRITER_PWD = #{writerPwd}
	,NOTICE_FLG = #{noticeFlg}

	,SECRET_FLG = #{secretFlg}
	,VIEW_START_DT = #{viewStartDt}
	,VIEW_END_DT = #{viewEndDt}

	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE
	,LAST_OPER_IP = #{lastOperIp}
WHERE
ARTICLE_KEY = #{articleKey}

UPDATE TB_ARTICLE 
SET VIEW_CNT = VIEW_CNT + 1 
WHERE ARTICLE_KEY = #{articleKey}

mapper.select:
SELECT * FROM (
	SELECT * FROM TB_ARTICLE
	WHERE PKEY = #{pkey}
	ORDER BY OKEY DESC
)T
LIMIT 1

mapper.update:
UPDATE TB_ARTICLE 
SET OKEY = OKEY + 1
WHERE FKEY = #{fkey} AND OKEY >= #{okey}

UPDATE TB_ARTICLE SET 
	DELETE_FLG = 'Y'
	,DELETE_TYP = #{deleteTyp}
	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE
	,LAST_OPER_IP = #{lastOperIp}
WHERE ARTICLE_KEY = #{articleKey}

UPDATE TB_ARTICLE SET 
	DELETE_FLG = 'N' 
	,DELETE_TYP = #{deleteTyp}
	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE
	,LAST_OPER_IP = #{lastOperIp}
WHERE ARTICLE_KEY = #{articleKey}
