mapper.select:
select * from ( SELECT row_number() over ( ORDER BY
USER_NM ASC FRST_OPER_DT DESC USER_NM ASC
USER_NM ASC
) as num ,T.USER_ID ,T.USER_PW ,T.USER_NM ,T.CHR_TEL ,T.CHR_MOB ,T.CHR_FAX ,T.CHR_EMAIL ,T.CHR_ZIP ,T.CHR_ADDR1 ,T.CHR_ADDR2 ,T.CORP_ID ,T.CORP_NM ,T.CORP_TEL ,T.CORP_MOB ,T.CORP_FAX ,T.CORP_EMAIL ,T.CORP_ZIP ,T.CORP_ADDR1 ,T.CORP_ADDR2 ,T.REG_ST ,T.AUTH_ID ,(SELECT AUTH_NM FROM TB_USER_AUTH UA WHERE UA.AUTH_ID = T.AUTH_ID) AUTH_NM ,T.LAST_LOGIN_DT ,T.FRST_OPER_DT ,T.LAST_OPER_DT FROM TB_CORP_USER T
ORDER BY
USER_NM ASC FRST_OPER_DT DESC USER_NM ASC
USER_NM ASC
) where num between #{firstRecordIndex} and #{recordCountPerPage}

mapper.update:
UPDATE TB_CORP_USER SET


		USER_PW = #{userPw}

