mapper.select:
select * from ( SELECT row_number() over ( ORDER BY
USER_NM ASC FRST_OPER_DT DESC USER_NM ASC
USER_NM ASC
) as num ,T.USER_ID ,T.USER_PW ,T.USER_NM ,T.TEL ,T.MOB ,T.EMAIL ,T.ZIP ,T.ADDR1 ,T.ADDR2 ,T.REG_ST ,T.AUTH_ID ,(SELECT AUTH_NM FROM TB_USER_AUTH UA WHERE UA.AUTH_ID = T.AUTH_ID) AUTH_NM ,T.LAST_LOGIN_DT ,T.FRST_OPER_DT ,T.LAST_OPER_DT FROM TB_GENR_USER T
ORDER BY
USER_NM ASC FRST_OPER_DT DESC USER_NM ASC
USER_NM ASC
) where num as between #{firstRecordIndex} and #{recordCountPerPage}

mapper.update:
UPDATE TB_GENR_USER SET 


			USER_PW = #{userPw}

		,USER_NM = #{userNm}
		,TEL = #{tel}
