		,EMAIL = #{email}
		,ZIP = #{zip}
		,ADDR1 = #{addr1}
		,ADDR2 = #{addr2}
		,REG_ST = #{regSt}
		,AUTH_ID = #{authId}
		,LAST_OPER_DT = SYSDATE

WHERE USER_ID = #{userId}

UPDATE TB_GENR_USER SET 

		TEL = #{tel}
		,MOB = #{mob}
		,EMAIL = #{email}
		,ZIP = #{zip}
		,ADDR1 = #{addr1}
