where.trim:

	AND T.LOCATE_TYP = #{locateTyp}	


	AND T.ACT_FLG = #{actFlg}	

AND ( 
		T.PERIOD_FLG = 'N' OR (T.PERIOD_FLG = 'Y' AND (T.START_DT <= SYSDATE() AND T.END_DT >= SYSDATE())) 
	)

mapper.insert:

	SELECT IFNULL(MAX(LINK_KEY), 0) + 1 linkKey FROM TB_LINK 

INSERT INTO TB_LINK (
	LINK_KEY
	,LOCATE_TYP
	,TITLE
	,URL
	,TARGET_TYP
	,PERIOD_FLG
	,START_DT
	,END_DT
	,ACT_FLG
	,VIEW_ORD
	,FRST_OPER_ID
	,FRST_OPER_DT
	,LAST_OPER_ID
	,LAST_OPER_DT
) VALUES (
	#{linkKey}
	,#{locateTyp}
	,#{title}
	,#{url}
	,#{targetTyp}
	,#{periodFlg}
	,#{startDt}
	,#{endDt}
	,#{actFlg}
	,0
	,#{frstOperId}
	,SYSDATE()
	,NULL
	,NULL
)

mapper.update:
UPDATE TB_LINK SET 
	LOCATE_TYP = #{locateTyp}
	,TITLE = #{title}
	,URL = #{url}
	,TARGET_TYP = #{targetTyp}
	,PERIOD_FLG = #{periodFlg}
	,START_DT = #{startDt}
	,END_DT = #{endDt}
	,ACT_FLG = #{actFlg}

		,VIEW_ORD = #{viewOrd}

	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE()
WHERE
LINK_KEY = #{linkKey}
