mapper.select:
SELECT * FROM TB_MENU
WHERE MENU_TYP = #{menuTyp}
ORDER BY FKEY, OKEY

mapper.update:
UPDATE TB_MENU
SET

	MENU_NM = #{menuNm}
	,NAVI_FLG = #{naviFlg}
	,LINK_TYP = #{linkTyp}
	,BOARD_KEY = #{boardKey}
	,CONTENT_KEY = #{contentKey}
	,URL = #{url}
	,SUB_VIEW_TYP = #{subViewTyp}
	,ACT_FLG = #{actFlg}
	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE

WHERE
	MENU_KEY=#{menuKey}


UPDATE TB_MENU
SET
	MENU_NM = #{menuNm}
	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE
WHERE
	MENU_KEY=#{menuKey}

UPDATE TB_MENU
SET
	MAIN_TOP_IMG_ON = #{mainTopImgOnFile}
	,MAIN_TOP_IMG_OFF = #{mainTopImgOffFile}
	,TOP_IMG_ON = #{topImgOnFile}
	,TOP_IMG_OFF = #{topImgOffFile}
	,LEFT_TOP_IMG = #{leftTopImgFile}
	,LEFT_IMG_ON = #{leftImgOnFile}
	,LEFT_IMG_OFF = #{leftImgOffFile}
	,TITLE_IMG = #{titleImgFile}
WHERE
MENU_KEY = #{menuKey}

UPDATE TB_MENU SET
MAIN_TOP_IMG_ON = NULL MAIN_TOP_IMG_OFF = NULL TOP_IMG_ON = NULL TOP_IMG_OFF = NULL LEFT_TOP_IMG = NULL LEFT_IMG_ON = NULL LEFT_IMG_OFF = NULL TITLE_IMG = NULL
WHERE MENU_KEY = #{menuKey}

UPDATE TB_MENU 
SET ACT_FLG = #{actFlg}
WHERE MENU_KEY = #{menuKey}

mapper.select:
SELECT * FROM TB_MENU
WHERE MENU_TYP = #{menuTyp}
AND FKEY = #{fkey}

SELECT MAX(FKEY) FKEY
FROM (
	SELECT MAX(FKEY) FKEY FROM TB_MENU
	WHERE MENU_TYP = #{menuTyp}
	AND FKEY < #{fkey}
	GROUP BY FKEY
	ORDER BY FKEY
) A

SELECT MIN(FKEY) FKEY
FROM (
	SELECT MAX(FKEY) FKEY FROM TB_MENU
	WHERE MENU_TYP = #{menuTyp}
	AND FKEY > #{fkey}
	GROUP BY FKEY
	ORDER BY FKEY
) A

mapper.update:
UPDATE TB_MENU 
SET FKEY = #{fkey}
WHERE MENU_TYP = #{menuTyp}
AND MENU_KEY = #{menuKey}

mapper.select:
SELECT B.* FROM (
		SELECT A.* FROM TB_MENU A
		WHERE MENU_TYP = #{menuTyp}
    AND PKEY = #{pkey}
		AND OKEY < #{okey}
		ORDER BY OKEY DESC
) B
WHERE ROWNUM<=1

SELECT B.* FROM (
		SELECT * FROM TB_MENU A
		WHERE MENU_TYP = #{menuTyp}
    AND PKEY = #{pkey}
		AND OKEY > #{okey}
		ORDER BY OKEY ASC
) B
WHERE ROWNUM<=1

mapper.update:
UPDATE TB_MENU 
SET OKEY = #{okey}
WHERE MENU_TYP = #{menuTyp}
AND MENU_KEY = #{menuKey}
