mapper.sql:
WITH PROPOSAL_MASTER AS ( SELECT ROW_NUMBER() OVER( ORDER BY TO_NUMBER(T.PROP_SEQ) ASC ) IDX, ROW_NUMBER() OVER( ORDER BY TO_NUMBER(T.PROP_SEQ) DESC ) ROWIDX, T.* FROM( SELECT PROP_SEQ, PROP_NAME, PROP_CATEGORY_CODE, GET_CODE_NM('PP_CT', PROP_CATEGORY_CODE) AS PROP_CATEGORY_CODE_NM, PROP_TYPE_CODE, GET_CODE_NM('PP_TY', PROP_TYPE_CODE) AS PROP_TYPE_CODE_NM, PROP_PROBLEM, PROP_IMPROVEMENT_CONT, PROP_BENEFIT, PROP_YEAR_EFFECT, PROP_YEAR_EFFECT_CODE, GET_CODE_NM('RESULTTY', PROP_YEAR_EFFECT_CODE) AS PROP_YEAR_ERRECT_CODE_NM, PROP_REL_MEMO_YN, PROP_REL_MEMO_CODE, PROP_GROUP_NAME, PROP_BELT_CODE, PROP_BIZ_PLACE_CODE, PROP_CIRCLE_CODE, PROP_APPROVER_CODE, PROP_PROP_STAT_CODE, GET_CODE_NM('PP_CL', PROP_PROP_STAT_CODE) AS PROP_PROP_STAT_CODE_NM, PROP_EVAL_SCORE, PROP_EFFECT_SCORE, PROP_SUBSIDY, PROP_PAYMENT_YN, PROP_PAYMENT_DATE, TO_CHAR(PROP_DATE, 'YYYY-MM-DD') AS PROP_DATE, TO_CHAR(PROP_PRACTICE_COMP_DATE, 'YYYY-MM-DD') AS PROP_PRACTICE_COMP_DATE, PROP_USER, GET_USER_NM(PROP_USER) AS PROP_USER_NM FROM TB_PROPOSAL_DETAIL
PROP_SEQ = #{searchPropSeq}
AND PROP_DATE BETWEEN TO_DATE(#{searchPropFromDate}, 'YYYY-MM-DD') AND TO_DATE(#{searchPropToDate}, 'YYYY-MM-DD')
AND PROP_CATEGORY_CODE = #{searchPropCategoryCode}
AND PROP_NAME LIKE '%'||#{searchPropName}||'%'
AND PROP_TYPE_CODE = #{searchPropTypeCode}
AND PROP_PROP_STAT_CODE = #{searchPropClassCode}
) T ) SELECT T.* FROM PROPOSAL_MASTER T
