mapper.insert:
INSERT INTO tb_report_team 
	(REP_TEAM_CODE
	, REP_CODE
	, COM_NO
	, DEPT_CODE
	, CIR_CODE
	, DEPT_NAME
	, COM_JOBX_CODE
	, COM_POSITION_CODE
	, BELT_CODE
	, REP_TEAM_MEM_NAME
	, REP_TEAM_MEM_EDU_CODE
	, REP_TEAM_MEM_ROLE
	, REP_TEAM_REG_USER
	, REP_TEAM_REG_DATE
	, REP_TEAM_MEM_STATUS) 
values 
	(
	#{repTeamCode}
	,#{repCode}
	,#{comNo}
	,#{deptCode}
	,#{cirCode}
	,#{deptName}
	,#{comJobxCode}
	,#{comPositionCode}
	,#{beltCode}
	,#{repTeamMemName}
	,#{repTeamMemEduCode}
	,#{repTeamMemRole}
	,#{repTeamRegUser}
	,sysdate
	,#{repTeamMemStatus}
	)

mapper.update:
UPDATE TB_REPORT_TEAM 
   SET  REP_CODE = #{repCode}
	    , COM_NO = #{comNo}
	    , DEPT_CODE = #{deptCode}
	    , CIR_CODE = #{cirCode}
	    , DEPT_NAME = #{deptName}
	    , COM_JOBX_CODE = #{comJobxCode}
	    , COM_POSITION_CODE = #{comPositionCode}
	    , BELT_CODE = #{beltCode}
	    , REP_TEAM_MEM_NAME = #{repTeamMemName}
	    , REP_TEAM_MEM_EDU_CODE = #{repTeamMemEduCode}
	    , REP_TEAM_MEM_ROLE = #{repTeamMemRole}
	    , REP_TEAM_UPDATE_USER = #{repTeamRegUser}
	    , REP_TEAM_UPDATE_DATE = sysdate
  WHERE REP_TEAM_CODE = #{repTeamCode}
