mapper.update:
UPDATE TB_CODE	
SET 
	CODE_NM = #{codeNm}
	,ATTR1 = #{attr1}
	,ATTR2 = #{attr2}
	,ATTR3 = #{attr3}
	,VIEW_ORD = #{viewOrd}
	,ACT_FLG = #{actFlg}
	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE
WHERE CODE_GRP_ID = #{codeGrpId} 
AND CODE_ID = #{codeId}

mapper.delete:
DELETE FROM TB_CODE
WHERE CODE_GRP_ID = #{codeGrpId} 
AND CODE_ID = #{codeId}
