mapper.insert:

	SELECT IFNULL(MAX(MENU_STAT_KEY), 0) + 1 menuStatKey FROM TB_MENU_STAT_${ym}

INSERT INTO TB_MENU_STAT_${ym} (
	MENU_STAT_KEY
	,YEAR
	,MONTH
	,DAY
	,HOUR
	,MINUTE
	,SECOND
	,STAT_TYP
	,SESSION_ID
	,URL
	,LOCALE
	,LANG
	,CNTRY
	,DSPLY_LANG
	,DSPLY_CNTRY
	,IP
	,REFERER
	,USER_AGENT
	,MENU_KEY
) VALUES (
	#{menuStatKey}
	,#{year}
	,#{month}
	,#{day}
	,#{hour}
	,#{minute}
	,#{second}
	,#{statTyp}
	,#{sessionId}
	,#{url}
	,#{locale}
	,#{lang}
	,#{cntry}
	,#{dsplyLang}
	,#{dsplyCntry}
	,#{ip}
	,#{referer}
	,#{userAgent}
	,#{menuKey}
)
