where.trim:

	AND T.LOCATE_TYP = #{locateTyp}	


	AND T.ACT_FLG = #{actFlg}	

AND ( 
		T.PERIOD_FLG = 'N' OR (T.PERIOD_FLG = 'Y' AND (T.START_DT <= SYSDATE AND T.END_DT >= SYSDATE)) 
	)

mapper.insert:

	SELECT IFNULL(MAX(POPUP_KEY), 0) + 1 popupKey FROM TB_POPUP

INSERT INTO TB_POPUP (
	POPUP_KEY
	,LOCATE_TYP
	,TITLE
	,PERIOD_FLG
	,START_DT
	,END_DT
	,SIZE_AUTO_FLG
	,SIZE_WDH
	,SIZE_HGT
	,CONTENT
	,ACT_FLG
	,VIEW_ORD
	,FRST_OPER_ID
	,FRST_OPER_DT
	,LAST_OPER_ID
	,LAST_OPER_DT
) VALUES (
	#{popupKey}
	,#{locateTyp}
	,#{title}
	,#{periodFlg}
	,#{startDt}
	,#{endDt}
	,#{sizeAutoFlg}
	,#{sizeWdh}
	,#{sizeHgt}
	,#{content}
	,#{actFlg}
	,0
	,#{frstOperId}
	,SYSDATE
	,NULL
	,NULL
)

mapper.update:
UPDATE TB_POPUP SET 
	POPUP_KEY = #{popupKey}
	,LOCATE_TYP = #{locateTyp}
	,TITLE = #{title}
	,PERIOD_FLG = #{periodFlg}
	,START_DT = #{startDt}
	,END_DT = #{endDt}
	,SIZE_AUTO_FLG = #{sizeAutoFlg}
	,SIZE_WDH = #{sizeWdh}
	,SIZE_HGT = #{sizeHgt}
	,CONTENT = #{content}
	,ACT_FLG = #{actFlg}

		,VIEW_ORD = #{viewOrd}

	,LAST_OPER_ID = #{lastOperId}
	,LAST_OPER_DT = SYSDATE
WHERE
POPUP_KEY = #{popupKey}
