mapper.sql:
WITH report_mst AS ( SELECT ROW_NUMBER() OVER( ORDER BY TO_NUMBER(T.rep_code) ASC ) idx, ROW_NUMBER() OVER( ORDER BY TO_NUMBER(T.rep_code) DESC ) rowidx, T.* FROM tb_report_mst T , (select rep_code, min(rep_plan_start_date) start_date , (case when rep_devision_code='1' then max(REP_PLAN_START_DATE) else max(REP_PLAN_END_DATE) end) as end_date from tb_report_detail group by rep_code, rep_devision_code) d WHERE t.rep_code=d.rep_code(+)
) SELECT idx, t.rep_code, t.rep_name, t.rep_devision_code, c_div.code_nm AS rep_division, t.rep_type_code, c_ty.code_nm AS rep_type, t.rep_status_code, c_st.code_nm AS rep_status, (select min(REP_PLAN_START_DATE) from tb_report_detail m where m.rep_code=t.rep_code) rep_start_date, (select case when rep_devision_code='1' then max(REP_PLAN_START_DATE) else max(REP_PLAN_END_DATE) end as rep_finish_date from tb_report_detail m where m.rep_code=T.rep_code group by rep_code, m.rep_devision_code) rep_finish_date
FROM report_mst T, tb_code c_div, tb_code c_ty, tb_code c_st WHERE T.rep_devision_code = c_div.code_id(+) AND c_div.code_grp_id(+) = '6SIG_YN' AND T.rep_type_code = c_ty.code_id(+) AND c_ty.code_grp_id(+) IN ('RP_TY1', 'RP_TY2', 'RP_TY3') AND T.rep_status_code = c_st.code_id(+) AND c_st.code_grp_id(+) = 'REP_STAT'

mapper.insert:
INSERT INTO TB_REPORT_MST (
	REP_CODE
	, REP_NAME
	, REP_MENU_CODE
	, REP_DEVISION_CODE
	, REP_TYPE_CODE
	, REP_SECTOR_CODE
	, REP_PRODUCT_CLASS
	, REP_LEADER_BELT_CODE
	, REP_ACTION_TYPE_CODE
	, REP_MBB_USE_RATE_CODE
	, REP_USE_REF_DATE
	, REP_STATUS_CODE

	, REP_KEYWORD
	, REP_REG_USER
	, REP_REG_DATE
	, REP_APPROVAL_CODE
	, REP_USE_YN
	, REP_MAIL_SEND_YN
	, REP_MAIL_SEND_DATE
	, REP_PLACE_CODE
	, REP_LEADER_CODE
	, REP_FILE_YN
) VALUES (
	#{repCode} 
	,#{repName} 
	,#{repMenuCode} 
	,#{repDivisionCode} 
	,#{repTypeCode} 
	,#{repSectorCode} 
	,#{repProductClass} 
	,#{repLeaderBeltCode} 
	,#{repActionTypeCode} 
	,#{repMbbUseRateCode} 
	,to_date(#{repUseRefDate}, 'YYYY') 
	,#{repStatusCode} 

	,#{repKeyword} 
	,#{repRegUser}
	, sysdate 
	,#{repApprovalCode} 
	,#{repUseYn} 
	,#{repMailSendYn} 
	,#{repMailSendDate} 
	,#{repPlaceCode} 
	,#{repLeaderCode} 
	,#{repFileYn} 
)

mapper.update:

UPDATE TB_REPORT_MST SET 
	REP_NAME = #{repName} 
	, REP_DEVISION_CODE = #{repDivisionCode} 
	, REP_TYPE_CODE = #{repTypeCode} 
	, REP_SECTOR_CODE = #{repSectorCode} 
	, REP_PRODUCT_CLASS = #{repProductClass} 
	, REP_LEADER_BELT_CODE = #{repLeaderBeltCode} 
	, REP_ACTION_TYPE_CODE = #{repActionTypeCode} 
	, REP_MBB_USE_RATE_CODE = #{repMbbUseRateCode}
	, REP_USE_REF_DATE = to_date(#{repUseRefDate}, 'YYYY') 
	, REP_STATUS_CODE = #{repStatusCode} 

	, REP_KEYWORD = #{repKeyword} 
	, REP_UPDATE_USER = #{repRegUser} 
	, REP_UPDATE_DATE = sysdate
	, REP_APPROVAL_CODE = #{repApprovalCode} 
	, REP_USE_YN = #{repUseYn} 
	, REP_MAIL_SEND_YN = #{repMailSendYn} 
	, REP_MAIL_SEND_DATE = #{repMailSendDate} 
	, REP_PLACE_CODE = #{repPlaceCode} 
	, REP_LEADER_CODE = #{repLeaderCode} 
	, REP_FILE_YN = #{repFileYn} 
WHERE
REP_CODE = #{repCode}

UPDATE TB_REPORT_MST
SET    REP_STATUS_CODE=#{repStatusCode} 
WHERE REP_CODE=#{repCode}

mapper.select:
with tb_report_result_custom as ( select rep_code , case when to_char(rep_result_type_code) in ('4','5','6','7') then '8' else to_char(rep_result_type_code) end as rep_result_type_code , rep_result_within_year , rep_result_year from tb_report_result ),raw_data as ( select get_code_nm('RESULTTY', rep_result_type_code) as rep_result_type , a.rep_result_type_code --, get_code_nm('ACTTYPE', T.REP_ACTION_TYPE_CODE) as REP_ACTION_TYPE , T.REP_ACTION_TYPE_CODE , a.rep_result_within_year as iny_total , case when T.REP_ACTION_TYPE_CODE='1' then to_number(a.rep_result_within_year) else null end as iny_act_1 , case when T.REP_ACTION_TYPE_CODE='2' then to_number(a.rep_result_within_year) else null end as iny_act_2 , case when T.REP_ACTION_TYPE_CODE='3' then to_number(a.rep_result_within_year) else null end as iny_act_3 , case when T.REP_ACTION_TYPE_CODE='4' then to_number(a.rep_result_within_year) else null end as iny_act_4 , case when T.REP_ACTION_TYPE_CODE='5' then to_number(a.rep_result_within_year) else null end as iny_act_5 , a.rep_result_year y_total , case when T.REP_ACTION_TYPE_CODE='1' then to_number(a.rep_result_year) else null end as y_act_1 , case when T.REP_ACTION_TYPE_CODE='2' then to_number(a.rep_result_year) else null end as y_act_2 , case when T.REP_ACTION_TYPE_CODE='3' then to_number(a.rep_result_year) else null end as y_act_3 , case when T.REP_ACTION_TYPE_CODE='4' then to_number(a.rep_result_year) else null end as y_act_4 , case when T.REP_ACTION_TYPE_CODE='5' then to_number(a.rep_result_year) else null end as y_act_5 from tb_report_result_custom a , tb_report_mst T , (select rep_code, min(rep_plan_start_date) start_date , (case when rep_devision_code='1' then max(REP_PLAN_START_DATE) else max(REP_PLAN_END_DATE) end) as end_date from tb_report_detail group by rep_code, rep_devision_code) d where a.rep_code=T.rep_code and t.rep_code=d.rep_code(+) -- 사업장, 부서, 조회기간은 작성자를 기준으로 검색한다. (5/17 이종일부장 확인)
), v_count as (
select count(iny_total) as iny_total , count(iny_act_1) as iny_act_1 , count(iny_act_2) as iny_act_2 , count(iny_act_3) as iny_act_3 , count(iny_act_4) as iny_act_4 , count(iny_act_5) as iny_act_5 , count(y_total) as y_total , count(y_act_1) as y_act_1 , count(y_act_2) as y_act_2 , count(y_act_3) as y_act_3 , count(y_act_4) as y_act_4 , count(y_act_5) as y_act_5 from raw_data ), v_summary as ( select rep_result_type , rep_result_type_code , sum(iny_total) as iny_total , sum(iny_act_1) as iny_act_1 , sum(iny_act_2) as iny_act_2 , sum(iny_act_3) as iny_act_3 , sum(iny_act_4) as iny_act_4 , sum(iny_act_5) as iny_act_5 , sum(y_total) as y_total , sum(y_act_1) as y_act_1 , sum(y_act_2) as y_act_2 , sum(y_act_3) as y_act_3 , sum(y_act_4) as y_act_4 , sum(y_act_5) as y_act_5 from raw_data group by rep_result_type, rep_result_type_code ) select '건수' as repResultType , c.iny_total, c.iny_act_1, c.iny_act_2, c.iny_act_3, c.iny_act_4, c.iny_act_5, c.y_total, c.y_act_1, c.y_act_2, c.y_act_3, c.y_act_4, c.y_act_5 from v_count c union all select c.code_nm as rep_result_type , nvl(s.iny_total, 0) iny_total , nvl(s.iny_act_1, 0) iny_act_1 , nvl(s.iny_act_2, 0) iny_act_2 , nvl(s.iny_act_3, 0) iny_act_3 , nvl(s.iny_act_4, 0) iny_act_4 , nvl(s.iny_act_5, 0) iny_act_5 , nvl(s.y_total, 0) y_total , nvl(s.y_act_1, 0) y_act_1 , nvl(s.y_act_2, 0) y_act_2 , nvl(s.y_act_3, 0) y_act_3 , nvl(s.y_act_4, 0) y_act_4 , nvl(s.y_act_5, 0) y_act_5 from (select * from tb_code where code_grp_id='RESULTTY' and code_id not in ('4','5','6','7') order by view_ord) c , v_summary s where c.code_id=s.rep_result_type_code(+)
