mapper.sql:
WITH report_mst AS ( SELECT ROW_NUMBER() OVER( ORDER BY TO_NUMBER(T.rep_code) ASC ) idx, ROW_NUMBER() OVER( ORDER BY TO_NUMBER(T.rep_code) DESC ) rowidx, T.* FROM tb_report_mst T , (select rep_code, min(rep_plan_start_date) start_date , (case when rep_devision_code='1' then max(REP_PLAN_START_DATE) else max(REP_PLAN_END_DATE) end) as end_date from tb_report_detail group by rep_code, rep_devision_code) d WHERE t.rep_code=d.rep_code(+)
) SELECT idx, t.rep_code, t.rep_name, t.rep_devision_code, c_div.code_nm AS rep_division, t.rep_type_code, c_ty.code_nm AS rep_type, t.rep_status_code, c_st.code_nm AS rep_status, (select min(REP_PLAN_START_DATE) from tb_report_detail m where m.rep_code=t.rep_code) rep_start_date, (select case when rep_devision_code='1' then max(REP_PLAN_START_DATE) else max(REP_PLAN_END_DATE) end as rep_finish_date from tb_report_detail m where m.rep_code=T.rep_code group by rep_code, m.rep_devision_code) rep_finish_date
FROM report_mst T, tb_code c_div, tb_code c_ty, tb_code c_st WHERE T.rep_devision_code = c_div.code_id(+) AND c_div.code_grp_id(+) = '6SIG_YN' AND T.rep_type_code = c_ty.code_id(+) AND c_ty.code_grp_id(+) IN ('RP_TY1', 'RP_TY2', 'RP_TY3') AND T.rep_status_code = c_st.code_id(+) AND c_st.code_grp_id(+) = 'REP_STAT'

mapper.insert:
INSERT INTO TB_REPORT_MST (
	REP_CODE
	, REP_NAME
	, REP_MENU_CODE
	, REP_DEVISION_CODE
	, REP_TYPE_CODE
	, REP_SECTOR_CODE
	, REP_PRODUCT_CLASS
	, REP_LEADER_BELT_CODE
	, REP_ACTION_TYPE_CODE
	, REP_MBB_USE_RATE_CODE
	, REP_USE_REF_DATE
	, REP_STATUS_CODE

	, REP_KEYWORD
	, REP_REG_USER
	, REP_REG_DATE
	, REP_APPROVAL_CODE
	, REP_USE_YN
	, REP_MAIL_SEND_YN
	, REP_MAIL_SEND_DATE
	, REP_PLACE_CODE
	, REP_LEADER_CODE
	, REP_FILE_YN
) VALUES (
	#{repCode} 
	,#{repName} 
	,#{repMenuCode} 
	,#{repDivisionCode} 
	,#{repTypeCode} 
	,#{repSectorCode} 
	,#{repProductClass} 
	,#{repLeaderBeltCode} 
	,#{repActionTypeCode} 
	,#{repMbbUseRateCode} 
	,to_date(#{repUseRefDate}, 'YYYY') 
	,#{repStatusCode} 

	,#{repKeyword} 
	,#{repRegUser}
	, sysdate 
	,#{repApprovalCode} 
	,#{repUseYn} 
	,#{repMailSendYn} 
	,#{repMailSendDate} 
	,#{repPlaceCode} 
	,#{repLeaderCode} 
	,#{repFileYn} 
)

mapper.update:

UPDATE TB_REPORT_MST SET 
	REP_NAME = #{repName} 
	, REP_DEVISION_CODE = #{repDivisionCode} 
	, REP_TYPE_CODE = #{repTypeCode} 
	, REP_SECTOR_CODE = #{repSectorCode} 
	, REP_PRODUCT_CLASS = #{repProductClass} 
	, REP_LEADER_BELT_CODE = #{repLeaderBeltCode} 
	, REP_ACTION_TYPE_CODE = #{repActionTypeCode} 
	, REP_MBB_USE_RATE_CODE = #{repMbbUseRateCode}
	, REP_USE_REF_DATE = to_date(#{repUseRefDate}, 'YYYY') 
	, REP_STATUS_CODE = #{repStatusCode} 

	, REP_KEYWORD = #{repKeyword} 
	, REP_UPDATE_USER = #{repRegUser} 
	, REP_UPDATE_DATE = sysdate
	, REP_APPROVAL_CODE = #{repApprovalCode} 
	, REP_USE_YN = #{repUseYn} 
	, REP_MAIL_SEND_YN = #{repMailSendYn} 
	, REP_MAIL_SEND_DATE = #{repMailSendDate} 
	, REP_PLACE_CODE = #{repPlaceCode} 
	, REP_LEADER_CODE = #{repLeaderCode} 
	, REP_FILE_YN = #{repFileYn} 
WHERE
REP_CODE = #{repCode}

mapper.select:
SELECT T.REP_CODE , T.REP_NAME , T.REP_MENU_CODE , T.REP_DEVISION_CODE , c_div.code_nm as REP_DIVISION , T.REP_TYPE_CODE , c_ty.code_nm as REP_TYPE , T.REP_SECTOR_CODE , c_sect.code_nm rep_sector , T.REP_PRODUCT_CLASS , T.REP_LEADER_BELT_CODE , c_belt.code_nm REP_LEADER_BELT , T.REP_ACTION_TYPE_CODE , c_act.code_nm REP_ACTION_TYPE , T.REP_MBB_USE_RATE_CODE , c_mbbr.code_nm REP_MBB_USE_RATE , to_char(T.REP_USE_REF_DATE, 'YYYY') REP_USE_REF_DATE , T.REP_STATUS_CODE , T.REP_KEYWORD , T.REP_REG_USER , T.REP_REG_DATE , T.REP_UPDATE_USER , T.REP_UPDATE_DATE , T.REP_APPROVAL_CODE , T.REP_USE_YN , T.REP_MAIL_SEND_YN , T.REP_MAIL_SEND_DATE , T.REP_PLACE_CODE , T.REP_LEADER_CODE , (select user_name from tb_user_mst where com_no=T.REP_LEADER_CODE) REP_LEADER_NAME , T.REP_FILE_YN
FROM TB_REPORT_MST T, tb_code c_div, tb_code c_ty, tb_code c_sect, tb_code c_st, tb_code c_belt, tb_code c_act, tb_code c_mbbr
WHERE T.rep_devision_code = c_div.code_id(+) AND c_div.code_grp_id(+) = '6SIG_YN' AND T.rep_type_code = c_ty.code_id(+) AND c_ty.code_grp_id(+) IN ('RP_TY1', 'RP_TY2', 'RP_TY3') AND T.rep_status_code = c_st.code_id(+) AND c_st.code_grp_id(+) = 'REP_STAT' AND T.REP_SECTOR_CODE = c_sect.code_id(+) AND c_sect.code_grp_id(+) = 'SECTOR' AND T.REP_LEADER_BELT_CODE = c_belt.code_id(+) AND c_belt.code_grp_id(+) = 'LDRBELT' AND T.REP_ACTION_TYPE_CODE = c_act.code_id(+) AND c_act.code_grp_id(+) = 'ACTTYPE' AND T.REP_MBB_USE_RATE_CODE = c_mbbr.code_id(+) AND c_mbbr.code_grp_id(+) = 'MBBUSERT' AND T.REP_CODE = #{repCode}
